$(document).ready(function () {

    var today = new Date();
    var dd = String(today.getDate()).padStart(2, '0');
    var mm = String(today.getMonth() + 1).padStart(2, '0');
    var yyyy = today.getFullYear();

    today = dd + '/' + mm + '/' + yyyy;
    var tomorrow = (parseInt(dd) + 1) + '/' + mm + '/' + yyyy;

    $('#date-pick .input-daterange').datepicker({
        format: "dd/mm/yyyy",
        todayBtn: true,
        todayHighlight: true,
        toggleActive: true,
        startDate: today,
    });

    $('input#ArrivalDate').val(today);
    $('input#DepartureDate').val(tomorrow);

    var map = L.map('mapid', {
        maxZoom: 24,
        minZoom: 1,
    }).setView([51.505, -0.09], 20);

    L.imageOverlay('/Images/camping-site.jpg', {
        noWrap: true,
    }).addTo(map);

    var myIcon = L.icon({
        iconUrl: '/Images/marker-icon.png',
        iconSize: [1000, 1000],
        iconAnchor: [22, 94],
        popupAnchor: [-3, -76],
        shadowUrl: '/Images/marker-shadow.png',
        shadowSize: [41, 41],
        shadowAnchor: [22, 94]
    });

    L.marker([100, 100], { icon: myIcon }).addTo(map);
});


function updatePeopleText() {
    var adults = $('input[type=number]#adultCount');
    var children = $('input[type=number]#childCount');
    var pets = $('input[type=number]#petCount');

    var text = (adults.val() > 1 ? adults.val() + " Voksne" : "1 Voksen");

    if (children.val() > 0)
        text += (children.val() > 1 ? ", " + children.val() + " B&oslash;rn" : ", 1 Barn");

    if (pets.val() > 0)
        text += "," + pets.val() + " Dyr";

    $('input[type=text]#peopleText').val(text);
}